using System;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using PinPadSDK.Commands;
using PinPadSDK.Enums;

namespace PinPadSdkTests.Commands {
    [TestClass]
    public class FncRequestTests {
        [TestMethod]
        public void ValidateFncRequestWarnings() {
            FncRequest request = new FncRequest();

            BasePropertyTestUtils.TestProperty<AcquirerCommunicationStatus>(request, request.FNC_COMMST, 1);
            BasePropertyTestUtils.TestProperty(request, request.FNC_ISSMODE, 1);
            BasePropertyTestUtils.TestProperty(request, request.FNC_ARC, 2);
            BasePropertyTestUtils.TestProperty(request, request.FNC_ISSDAT, 512);

            Assert.AreEqual("",
                request.CommandString);
            BasePropertyTestUtils.TestCommandString(request);

            BasePropertyTestUtils.TestProperty(request, request.FNC_TAGS, 256, true);

            Assert.AreEqual("",
                request.CommandString);
            BasePropertyTestUtils.TestCommandString(request);
        }
    }
}
